import axios from 'axios';

// Netlify Functions API 기본 URL
const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:8888/.netlify/functions/api';

// axios 인스턴스 생성
const api = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// 요청 인터셉터 (토큰 추가)
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('token');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// 응답 인터셉터 (에러 처리)
api.interceptors.response.use(
  (response) => response,
  (error) => {
    if (error.response?.status === 401) {
      localStorage.removeItem('token');
      localStorage.removeItem('user');
      window.location.href = '/login';
    }
    return Promise.reject(error);
  }
);

// 사용자 관련 API
export const userAPI = {
  // 회원가입
  register: (userData) => api.post('/users/register', userData),
  
  // 로그인
  login: (credentials) => api.post('/users/login', credentials),
  
  // 사용자 목록 조회
  getUsers: () => api.get('/users'),
};

export const loginUser = async (email, password) => {
  try {
    const response = await api.post('/users/login', { email, password });
    return response.data;
  } catch (error) {
    console.error('로그인 오류:', error.response?.data || error.message);
    throw error;
  }
};

export const registerUser = async (username, email, password) => {
  try {
    const response = await api.post('/users/register', { username, email, password });
    return response.data;
  } catch (error) {
    console.error('회원가입 오류:', error.response?.data || error.message);
    throw error;
  }
};

// 예약 관련 API
export const createBooking = async (bookingData) => {
  try {
    const token = localStorage.getItem('token');
    const response = await api.post('/bookings', bookingData, {
      headers: { Authorization: `Bearer ${token}` }
    });
    return response.data;
  } catch (error) {
    console.error('예약 생성 오류:', error.response?.data || error.message);
    throw error;
  }
};

export const getUserBookings = async () => {
  try {
    const token = localStorage.getItem('token');
    const response = await api.get('/bookings', {
      headers: { Authorization: `Bearer ${token}` }
    });
    return response.data;
  } catch (error) {
    console.error('사용자 예약 조회 오류:', error.response?.data || error.message);
    throw error;
  }
};

export const getBooking = async (id) => {
  try {
    const token = localStorage.getItem('token');
    const response = await api.get(`/bookings/${id}`, {
      headers: { Authorization: `Bearer ${token}` }
    });
    return response.data;
  } catch (error) {
    console.error('예약 상세 조회 오류:', error.response?.data || error.message);
    throw error;
  }
};

export const updateBooking = async (id, bookingData) => {
  try {
    const token = localStorage.getItem('token');
    const response = await api.put(`/bookings/${id}`, bookingData, {
      headers: { Authorization: `Bearer ${token}` }
    });
    return response.data;
  } catch (error) {
    console.error('예약 수정 오류:', error.response?.data || error.message);
    throw error;
  }
};

