import axios from 'axios';

// Netlify Functions API 기본 URL
const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:8888/.netlify/functions/api';

// axios 인스턴스 생성
const api = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// 요청 인터셉터 (토큰 추가)
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('token');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// 응답 인터셉터 (에러 처리)
api.interceptors.response.use(
  (response) => response,
  (error) => {
    if (error.response?.status === 401) {
      localStorage.removeItem('token');
      localStorage.removeItem('user');
      window.location.href = '/login';
    }
    return Promise.reject(error);
  }
);

// 사용자 관련 API
export const userAPI = {
  // 회원가입
  register: (userData) => api.post('/users/register', userData),
  
  // 로그인
  login: (credentials) => api.post('/users/login', credentials),
  
  // 사용자 목록 조회
  getUsers: () => api.get('/users'),
};

// 예약 관련 API
export const createBooking = async (bookingData) => {
  console.log('createBooking 호출됨');
  console.log('전송할 예약 데이터:', bookingData);
  console.log('API URL:', API_BASE_URL);
  
  try {
    const response = await api.post('/bookings', bookingData);
    console.log('예약 생성 성공:', response.data);
    return response.data;
  } catch (error) {
    console.error('예약 생성 실패:', error);
    console.error('에러 응답:', error.response?.data);
    console.error('에러 상태:', error.response?.status);
    throw error;
  }
};

export const getBookings = async () => {
  const response = await api.get('/bookings');
  return response.data;
};

// 예약 수정
export const updateBooking = (id, bookingData) => api.put(`/bookings/${id}`, bookingData);

// 예약 삭제
export const deleteBooking = (id) => api.delete(`/bookings/${id}`);

// 객실 관련 API
export const getRooms = async () => {
  const response = await api.get('/rooms');
  return response.data;
};

// 갤러리 관련 API
export const getGallery = async () => {
  const response = await api.get('/gallery');
  return response.data;
};

export default api;
