import axios from 'axios';

// Netlify Functions API 기본 URL
const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:8888/.netlify/functions/api';

// axios 인스턴스 생성
const api = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// 요청 인터셉터 (토큰 추가)
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('token');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// 응답 인터셉터 (에러 처리)
api.interceptors.response.use(
  (response) => response,
  (error) => {
    if (error.response?.status === 401) {
      localStorage.removeItem('token');
      localStorage.removeItem('user');
      window.location.href = '/login';
    }
    return Promise.reject(error);
  }
);

// 사용자 관련 API
export const userAPI = {
  // 회원가입
  register: (userData) => api.post('/users/register', userData),
  
  // 로그인
  login: (credentials) => api.post('/users/login', credentials),
  
  // 사용자 목록 조회
  getUsers: () => api.get('/users'),
};

// 예약 관련 API
export const createBooking = async (bookingData) => {
  try {
    console.log('예약 데이터 전송:', bookingData);
    
    // Netlify Functions API 형식에 맞게 데이터 변환
    const requestData = {
      roomId: bookingData.roomId,
      checkIn: bookingData.checkIn,
      checkOut: bookingData.checkOut,
      guests: bookingData.guests,
      guestName: bookingData.guestInfo.guestName,
      guestEmail: bookingData.guestInfo.guestEmail,
      guestPhone: bookingData.guestInfo.guestPhone || '',
      specialRequests: bookingData.guestInfo.specialRequests || ''
    };
    
    console.log('변환된 예약 데이터:', requestData);
    
    const response = await api.post('/bookings', requestData);
    console.log('예약 생성 응답:', response.data);
    return response.data;
  } catch (error) {
    console.error('예약 생성 오류:', error.response?.data || error.message);
    throw error;
  }
};

export const getBookings = async () => {
  try {
    const response = await api.get('/bookings');
    return response.data;
  } catch (error) {
    console.error('예약 조회 오류:', error.response?.data || error.message);
    throw error;
  }
};

// 예약 수정
export const updateBooking = (id, bookingData) => api.put(`/bookings/${id}`, bookingData);

// 예약 삭제
export const deleteBooking = (id) => api.delete(`/bookings/${id}`);

// 객실 관련 API
export const getRooms = async () => {
  try {
    const response = await api.get('/rooms');
    return response.data;
  } catch (error) {
    console.error('객실 조회 오류:', error.response?.data || error.message);
    throw error;
  }
};

// 갤러리 관련 API
export const getGallery = async () => {
  try {
    const response = await api.get('/gallery');
    return response.data;
  } catch (error) {
    console.error('갤러리 조회 오류:', error.response?.data || error.message);
    throw error;
  }
};

export default api;
