import axios from 'axios';

// Netlify Functions API 기본 URL
const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:8888/.netlify/functions/api';

// axios 인스턴스 생성
const api = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// 요청 인터셉터 (토큰 추가)
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('token');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// 응답 인터셉터 (에러 처리)
api.interceptors.response.use(
  (response) => response,
  (error) => {
    if (error.response?.status === 401) {
      localStorage.removeItem('token');
      localStorage.removeItem('user');
      window.location.href = '/login';
    }
    return Promise.reject(error);
  }
);

// 사용자 관련 API
export const userAPI = {
  // 회원가입
  register: (userData) => api.post('/users/register', userData),
  
  // 로그인
  login: (credentials) => api.post('/users/login', credentials),
  
  // 사용자 목록 조회
  getUsers: () => api.get('/users'),
};

// 예약 관련 API
export const bookingAPI = {
  // 예약 목록 조회
  getBookings: () => api.get('/bookings'),
  
  // 예약 생성
  createBooking: (bookingData) => api.post('/bookings', bookingData),
  
  // 예약 수정
  updateBooking: (id, bookingData) => api.put(`/bookings/${id}`, bookingData),
  
  // 예약 삭제
  deleteBooking: (id) => api.delete(`/bookings/${id}`),
};

// 객실 관련 API
export const getRooms = async () => {
  console.log('getRooms API 호출 시작');
  console.log('API Base URL:', API_BASE_URL);
  console.log('요청 URL:', `${API_BASE_URL}/rooms`);
  
  try {
    console.log('axios.get 호출 중...');
    const response = await api.get('/rooms');
    console.log('getRooms API 응답 성공:', response);
    console.log('응답 데이터:', response.data);
    return response.data;
  } catch (error) {
    console.error('getRooms API 오류:', error);
    console.error('오류 상세 정보:', {
      message: error.message,
      status: error.response?.status,
      statusText: error.response?.statusText,
      data: error.response?.data
    });
    throw error;
  }
};

export default api;
